pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
*
* Implementation of a diamond.
/******************************************************************************/

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/interfaces/IERC165.sol";
import "@openzeppelin/contracts/interfaces/IERC721.sol";
import "@openzeppelin/contracts/interfaces/IERC721Metadata.sol";

import "./interfaces/IERC721Enumerable.sol";
import "./libraries/DiamondLib.sol";
import { LibDiamond } from "./libraries/LibDiamond.sol";
import { DiamondLib } from "./libraries/DiamondLib.sol";
import { IDiamondCut } from "./interfaces/IDiamondCut.sol";
import { DiamondSettings } from "./interfaces/IDiamond.sol";
import { IERC173 } from "./interfaces/IERC173.sol";

/**
 * @title Diamond
 * @dev Core diamond proxy contract implementing the EIP-2535 Diamond Standard
 * Functionality has been separated into facets:
 * - DiamondCutFacet: Handles diamond cut operations
 * - DiamondLoupeFacet: Provides introspection functions
 * - OwnershipFacet: Handles ownership operations
 */
contract Diamond is Initializable, IERC173 {
    /**
     * @notice Initialize the Diamond contract
     * @param _owner The owner of the contract
     * @param params Diamond settings including name and symbol
     * @param _facets The initial facets to add
     * @param diamondInit The initialization contract
     * @param _calldata The initialization calldata
     */
    function initialize(
        address _owner, 
        DiamondSettings memory params,
        IDiamondCut.FacetCut[] memory _facets,
        address diamondInit,
        bytes calldata _calldata
    ) public initializer {
        // Set up interfaces
        LibDiamond.diamondStorage().supportedInterfaces[type(IERC165).interfaceId] = true;
        LibDiamond.diamondStorage().supportedInterfaces[type(IDiamondCut).interfaceId] = true;
        LibDiamond.diamondStorage().supportedInterfaces[type(IERC173).interfaceId] = true;
        LibDiamond.diamondStorage().supportedInterfaces[type(IERC721).interfaceId] = true;
        LibDiamond.diamondStorage().supportedInterfaces[type(IERC721Metadata).interfaceId] = true;
        LibDiamond.diamondStorage().supportedInterfaces[type(IERC721Enumerable).interfaceId] = true;

        // Initialize the diamond
        LibDiamond.diamondCut(_facets, diamondInit, _calldata);

        // Set the owner
        LibDiamond.setContractOwner(_owner);

        // Set the symbol and name of the diamond
        DiamondLib.diamondStorage().diamondContract.settings.owner = _owner;
        DiamondLib.diamondStorage().diamondContract.metadata['symbol'] = params.symbol;
        DiamondLib.diamondStorage().diamondContract.metadata['name'] = params.name;

        // Initialize the upgrade timelock
        LibDiamond.initializeUpgradeTimelock(LibDiamond.DEFAULT_UPGRADE_TIMELOCK);
    }

    /**
     * @notice Transfer ownership to a new address
     * @param _newOwner The new owner address
     */
    function transferOwnership(address _newOwner) external override {
        LibDiamond.enforceIsContractOwner();
        LibDiamond.setContractOwner(_newOwner);
    }

    /**
     * @notice Get the current contract owner
     * @return owner_ The current owner address
     */
    function owner() external override view returns (address owner_) {
        owner_ = LibDiamond.contractOwner();
    }

    /**
     * @notice Get this contract's address
     * @return This contract's address
     */
    function diamondAddress() external view returns (address) {
        return address(this);
    }

    /**
     * @dev Diamond Proxy fallback function
     * Find facet for function that is called and execute the
     * function using delegatecall
     */
    fallback() external payable {
        LibDiamond.DiamondStorage storage ds;
        bytes32 position = LibDiamond.DIAMOND_STORAGE_POSITION;
        // Get diamond storage
        assembly {
            ds.slot := position
        }
        // Get facet from function selector
        address facet = ds.selectorToFacetAndPosition[msg.sig].facetAddress;
        require(facet != address(0), "Diamond: Function does not exist");

        // Execute external function from facet using delegatecall and return any value
        assembly {
            // Copy function selector and any arguments
            calldatacopy(0, 0, calldatasize())
            // Execute function call using the facet
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)
            // Get any return value
            returndatacopy(0, 0, returndatasize())
            // Return any return value or error back to the caller
            switch result
                case 0 {
                    revert(0, returndatasize())
                }
                default {
                    return(0, returndatasize())
                }
        }
    }

    // Allow contract to receive ETH
    receive() external payable {}
}
","keccak256":"0x225a60ba30317470015674dad6486fa063eb01d451b3db232ea060e253bec2c2","license":"MIT"},"contracts/DiamondFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

import "./libraries/DiamondLib.sol";
import "./libraries/DiamondFactoryLib.sol";
import "./Diamond.sol";
import "./interfaces/IDiamondFactory.sol";
import "./interfaces/IMetadata.sol";
import "./interfaces/IControllable.sol";

import "./utilities/Controllable.sol";

/// @title Diamond Factory
/// @notice This contract is used to create new Diamond contracts.
contract DiamondFactory is Initializable, Controllable {

    using DiamondFactoryLib for DiamondFactoryLib.DiamondFactoryStorage;

    event DiamondCreated(
        address indexed factory,
        address indexed diamond,
        string indexed symbol,
        DiamondSettings settings
    );

    event DiamondAdd(
        address indexed factory,
        string indexed symbol,
        address indexed diamond
    );

    event DiamondRemoved(
        address indexed factory,
        string indexed symbol,
        address indexed diamond
    );

    constructor() {
        _addController(msg.sender);
    }

    /// @notice initiiate the factory
    /// @param initData the initialization data for the factory
    function initialize(DiamondFactoryInit memory initData) public initializer {
        DiamondFactoryLib.diamondFactoryStorage()._addFacetSet(
            initData.setName,
            initData.facetAddresses
        );
        DiamondFactoryLib
            .diamondFactoryStorage()
            .contractData
            .defaultFacetSet = initData.setName;
    }

    /// @notice get the facets for the diamond
    function getFacets(string memory facetSet)
        external
        view
        returns (IDiamondCut.FacetCut[] memory)
    {
        return
            DiamondFactoryLib.diamondFactoryStorage().contractData.facetsToAdd[
                facetSet
            ];
    }

    /// @notice set a number of template facets on this factory
    /// @param facetSet the index of the facet to set
    /// @param facetAddress the facet to set
    function setFacets(
        string memory facetSet,
        IDiamondCut.FacetCut[] memory facetAddress
    ) external onlyController {
        DiamondFactoryLib
            .diamondFactoryStorage()
            .contractData
            .defaultFacetSet = facetSet;
        DiamondFactoryLib.diamondFactoryStorage()._addFacetSet(
            facetSet,
            facetAddress
        );
    }

    /// @notice remote a facet set from the factory
    /// @param facetSet the facet set to remove
    function removeFacets(string memory facetSet) external onlyController {
        delete DiamondFactoryLib
            .diamondFactoryStorage()
            .contractData
            .facetsToAdd[facetSet];
    }

    /// @notice get the address of the diamond
    /// @param symbol the symbol of the diamond
    /// @return the address of the diamond
    function getDiamondAddress(string memory symbol)
        public
        view
        returns (address)
    {
        return
            DiamondFactoryLib.diamondFactoryStorage()._getDiamondAddress(
                address(this),
                symbol,
                type(Diamond).creationCode
            );
    }

    /// @notice create a new diamond token with the given symbol
    /// @param params diamond init parameters
    /// @param diamondInit the diamond init contract
    /// @param _calldata the calldata to pass to the diamond init contract
    function createFromSet(
        DiamondSettings memory params,
        address diamondInit,
        bytes calldata _calldata,
        string memory facets
    ) public onlyController returns (address payable diamondAddress) {
        // get the factory storage context, error if token already exists
        require(
            DiamondFactoryLib
                .diamondFactoryStorage()
                .contractData
                .diamondAddresses[params.symbol] == address(0),
            "exists"
        );
        diamondAddress = DiamondFactoryLib.diamondFactoryStorage().createFromSet(
            address(this),
            params,
            diamondInit,
            _calldata,
            type(Diamond).creationCode,
            facets
        );
        emit DiamondCreated(address(this), diamondAddress, params.symbol, params);
    }

    /// @notice check if the token exists
    /// @param symbol the symbol of the diamond to check
    function exists(string memory symbol) public view returns (bool) {
        return
            DiamondFactoryLib
                .diamondFactoryStorage()
                .contractData
                .diamondAddresses[symbol] != address(0);
    }

    /// @notice get all the symbols from the factory
    /// @return the symbols
    function symbols() public view returns (string[] memory) {
        return
            DiamondFactoryLib
                .diamondFactoryStorage()
                .contractData
                .diamondSymbols;
    }

}
","keccak256":"0x541a8ac06b36b565f6106ac302a2415dff89c4873a2680f75b84647e2f23d7cf","license":"MIT"},"contracts/identity/IdentityStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../interfaces/IERC721A.sol";
import {TrustedIssuer} from "../interfaces/ITrustedIssuersRegistry.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/interfaces/IERC721Receiver.sol";
import "@openzeppelin/contracts/interfaces/IERC721.sol";
import {IIdentity} from "../interfaces/IIdentity.sol";
import {Claim} from "../interfaces/IClaim.sol";
import "../interfaces/IMarketplace.sol";
import {MultiSaleStorage} from "../interfaces/IMultiSale.sol";
import "../libraries/TradeDealLib.sol";
import "../libraries/FeeDistributorLib.sol"; // Add import for FeeDistributorLib

import "hardhat/console.sol";

library IdentitySystemStorage {
    using IdentitySystemStorage for IdentitySystemStorage.IdentitySystem;
    using Address for address;
bytes32 internal constant MULTISALE_STORAGE_POSITION =
        keccak256("diamond.nextblock.bitgem.app.MultiSaleStorage.storage");

    function multiSaleStorage()
        internal
        pure
        returns (MultiSaleStorage storage ds)
    {
        bytes32 position = MULTISALE_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    // CollateralStorage struct definition
    struct CollateralStorage {
        address nft;
        address invoicePool;
        address vabb;
        address usdcPool;
        address interest;
        uint256 interestRate;
        uint256 collateralToInterestRatio;
    }

    error ApprovalCallerNotOwnerNorApproved();
    error ApprovalQueryForNonexistentToken();
    error ApproveToCaller();
    error ApprovalToCurrentOwner();
    error BalanceQueryForZeroAddress();
    error MintedQueryForZeroAddress();
    error BurnedQueryForZeroAddress();
    error AuxQueryForZeroAddress();
    error MintToZeroAddress();
    error MintZeroQuantity();
    error OwnerIndexOutOfBounds();
    error OwnerQueryForNonexistentToken();
    error TokenIndexOutOfBounds();
    error TransferCallerNotOwnerNorApproved();
    error TransferFromIncorrectOwner();
    error TransferToNonERC721ReceiverImplementer();
    error TransferToZeroAddress();
    error URIQueryForNonexistentToken();

    struct IdentitySystem {
        // ERC721A storage
        ERC721AContract erc721Contract;
        // TrustedIssuer storage
        mapping(address => TrustedIssuer) trustedIssuers;
        address[] trustedIssuerAddresses;
        mapping(address => mapping(uint256 => bool)) trustedIssuerClaimTopics;
        mapping(address => uint256[]) trustedIssuerClaimTopicsList;
        mapping(uint256 => bool) topics;
        uint256[] topicList;
        // Identity storage
        mapping(address => address) identities;
        mapping(address => address) identityOwnersMap;
        address[] identityOwners;
        // Claim storage
        mapping(address => mapping(uint256 => uint256)) claims;
        mapping(address => uint256[]) claimList;
        // Claim storage
        mapping(uint256 => Claim) tokenClaims;
        Claim[] tokenClaimList;
        // ERC721Permissioned storage
        address delegatedMinter;
        mapping(uint256 => bool) frozen;

        uint256 itemIds;
        uint256 itemsSold;
        mapping(uint256 => MarketItem) idToMarketItem;
        mapping(uint256 => bool) idToListed;
        MarketItem[] items;

        // Merkle proof tracking for MultiSale
        mapping(bytes32 => bool) usedMerkleLeaves;

        // Collateral storage
        CollateralStorage collateralStorage;

        // Trade Deal storage
        TradeDealLib.TradeDealStorage tradeDealStorage;

        // Fee Distributor storage
        FeeDistributorLib.FeeDistributorStorage feeDistributorStorage;
    }

    bytes32 constant IDENTITY_SYSTEM_STORAGE_POSITION =
        keccak256("diamond.identity.system.storage");

    function diamondStorage()
        internal
        pure
        returns (IdentitySystemStorage.IdentitySystem storage ds)
    {
        bytes32 position = IDENTITY_SYSTEM_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function _tokenExists(
        IdentitySystem storage self,
        uint256 _tokenId
    ) internal view returns (bool) {
        return self.erc721Contract._ownerships[_tokenId].addr != address(0);
    }

    function _getIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (address addr) {
        return self.identities[_userAddress];
    }

    function getIdentityOwner(
        IdentitySystem storage self,
        address _identity
    ) internal view returns (address) {
        return self.identityOwnersMap[_identity];
    }

    function isIdentityAddress(
        IdentitySystem storage self,
        address _identity
    ) internal view returns (bool) {
        return self.identityOwnersMap[_identity] != address(0);
    }

    function isRegistered(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return self.identities[_userAddress] != address(0);
    }

    function isIdentityOwner(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return
            self.identityOwnersMap[self.identities[_userAddress]] ==
            _userAddress;
    }

    // trusted issuer

    function isTrustedIssuer(
        IdentitySystem storage self,
        address _issuer
    ) internal view returns (bool) {
        return self.trustedIssuers[_issuer].claimIssuer != address(0);
    }

    function addTrustedIssuer(
        IdentitySystem storage self,
        address _trustedIssuer,
        uint[] calldata _claimTopics
    ) internal {
        self.trustedIssuers[_trustedIssuer] = TrustedIssuer({
            claimIssuer: _trustedIssuer,
            claimTopics: _claimTopics
        });
        self.trustedIssuerAddresses.push(_trustedIssuer);
    }

    function removeTrustedIssuer(
        IdentitySystem storage self,
        address _trustedIssuer
    ) internal {
        delete self.trustedIssuers[_trustedIssuer];
        for (uint256 i = 0; i < self.trustedIssuerAddresses.length; i++) {
            if (self.trustedIssuerAddresses[i] == _trustedIssuer) {
                self.trustedIssuerAddresses[i] = self.trustedIssuerAddresses[
                    self.trustedIssuerAddresses.length - 1
                ];
                self.trustedIssuerAddresses.pop();
                break;
            }
        }
    }

    // claim topics

    function addClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal {
        self.topics[_claimTopic] = true;
        self.topicList.push(_claimTopic);
    }

    function removeClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal {
        delete self.topics[_claimTopic];
        for (uint256 i = 0; i < self.topicList.length; i++) {
            if (self.topicList[i] == _claimTopic) {
                self.topicList[i] = self.topicList[self.topicList.length - 1];
                self.topicList.pop();
                break;
            }
        }
    }

    function getClaimTopics(
        IdentitySystem storage self
    ) internal view returns (uint256[] memory) {
        return self.topicList;
    }

    function hasClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal view returns (bool) {
        return self.topics[_claimTopic];
    }

    // trusted issuer / claim topics

    function updateIssuerClaimTopics(
        IdentitySystem storage self,
        address _trustedIssuer,
        uint[] calldata _claimTopics
    ) internal {
        self.trustedIssuers[_trustedIssuer].claimTopics = _claimTopics;
        for (
            uint256 i = 0;
            i < self.trustedIssuerClaimTopicsList[_trustedIssuer].length;
            i++
        ) {
            delete self.trustedIssuerClaimTopics[_trustedIssuer][
                self.trustedIssuerClaimTopicsList[_trustedIssuer][i]
            ];
        }
    }

    function addTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint _claimTopic
    ) internal {
        self.trustedIssuerClaimTopics[_issuer][_claimTopic] = true;
        self.trustedIssuerClaimTopicsList[_issuer].push(_claimTopic);
    }

    function removeTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint _claimTopic
    ) internal {
        delete self.trustedIssuerClaimTopics[_issuer][_claimTopic];
        for (
            uint256 i = 0;
            i < self.trustedIssuerClaimTopicsList[_issuer].length;
            i++
        ) {
            if (self.trustedIssuerClaimTopicsList[_issuer][i] == _claimTopic) {
                self.trustedIssuerClaimTopicsList[_issuer][i] = self
                    .trustedIssuerClaimTopicsList[_issuer][
                        self.trustedIssuerClaimTopicsList[_issuer].length - 1
                    ];
                self.trustedIssuerClaimTopicsList[_issuer].pop();
                break;
            }
        }
    }

    modifier isTrustedIssuerForClaimTopic(uint256 _claimTopic) {
        IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        require(_storage.isTrustedIssuer(msg.sender), "Not a trusted issuer");
        require(
            _storage.hasTrustedIssuerClaimTopic(msg.sender, _claimTopic),
            "Issuer not authorized for claim topic"
        );
        _;
    }

    function hasTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint256 _claimTopic
    ) internal view returns (bool) {
        if (!self.isTrustedIssuer(_issuer)) return false;
        uint256[] storage claimTopics = self
            .trustedIssuers[_issuer]
            .claimTopics;
        for (uint256 i = 0; i < claimTopics.length; i++) {
            if (claimTopics[i] == _claimTopic) {
                return true;
            }
        }
        return false;
    }

    function addIdentity(
        IdentitySystem storage self,
        address _userAddress,
        address _identity
    ) internal {
        self.identities[_userAddress] = _identity;
        self.identityOwners.push(_userAddress);
        self.identityOwnersMap[_identity] = _userAddress;
    }

    function removeIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal {
        address identityAddress = self.identities[_userAddress]; // Capture identity address before deletion
        delete self.identities[_userAddress];
        for (uint256 i = 0; i < self.identityOwners.length; i++) {
            if (self.identityOwners[i] == _userAddress) {
                self.identityOwners[i] = self.identityOwners[
                    self.identityOwners.length - 1
                ];
                self.identityOwners.pop();
                self.identityOwnersMap[identityAddress] = address(0); // Use captured identityAddress
                break;
            }
        }
    }

    function hasIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        address identity = self.identities[_userAddress];
        return identity != address(0);
    }

    function hasClaims(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return self.identities[_userAddress] != address(0);
    }

    function isVerified(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return hasIdentity(self, _userAddress) && hasClaims(self, _userAddress);
    }

