        require(self.exists(tokenId), "ERC721: token does not exist");
        for (uint256 i = 0; i < claimTopics.length; i++) {
            self.tokenClaims[tokenId] = Claim({
                topic: claimTopics[i],
                scheme: 0,
                issuer: address(this),
                signature: "",
                data: "",
                uri: ""
            });
        }
    }

    function getTokenClaimTopics(uint256 tokenId) public view returns (Claim[] memory) {
        IdentitySystemStorage.IdentitySystem storage identitySystem = IdentitySystemStorage.diamondStorage();
        require(identitySystem._exists(tokenId), "ERC721: token does not exist");
        Claim[] memory claimTopics = new Claim[](identitySystem.tokenClaimList.length);
        for (uint256 i = 0; i < identitySystem.tokenClaimList.length; i++) {
            claimTopics[i] = identitySystem.tokenClaims[tokenId];
        }
        return claimTopics;
    }

