    }

    function removeClaim(address _address, uint256 _claimTopic) external override isTrustedIssuerForClaimTopic(_claimTopic) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        address idObj = _getIdentityAddress(_address);
        require(_storage.getIdentityOwner(idObj) != address(0), "Identity does not exist");
        require(idObj != msg.sender, "Cannot set claims for self");

        uint256[] storage claimList = _storage.claimList[idObj];
        for (uint256 i = 0; i < claimList.length; i++) {
            if (claimList[i] == _claimTopic) {
                // Move the last element to the position of the element to be removed
                claimList[i] = claimList[claimList.length - 1];
                // Remove the last element
                claimList.pop();

                // If you need to modify _storage.claims as well:
                delete _storage.claims[idObj][_claimTopic];

                emit ClaimRemoved(msg.sender, idObj, _claimTopic);
                return;
            }
        }

        revert("Claim does not exist");
    }

    /// @notice does an identity exist
    /// @param _userAddress address of the identity user
    /// @return bool
    function contains(address _userAddress) external view override returns (bool) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        return _storage._getIdentity(_userAddress) != address(0);
    }

    function containsAddress(address _userAddress) external view returns (bool) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        return _storage._getIdentity(_userAddress) != address(0);
    }

    /// @notice is an identity verified
    /// @param _address address of the identity user or identity contract
    /// @return bool
    function isVerified(address _address) external view override returns (bool) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        return _storage._getIdentity(_address) != address(0);
    }

    /// @notice get the users of the registry
    /// @return array of addresses of the users
    function getRegistryUsers() external view override returns (address[] memory) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        return _storage.identityOwners;
    }

    /// @notice is the user a registry user
    /// @param _registryUser address of the registry user
    /// @return bool
    function isRegistryUser(address _registryUser) external view override returns (bool) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        return _storage._getIdentity(_registryUser) != address(0);
    }

    function getClaims(address _address) external view override returns (uint256[] memory) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        address idObj = _getIdentityAddress(_address);
        return _storage.claimList[idObj];
    }

    function getClaim(address _address, uint256 _claimTopic) external view override returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        address idObj = _getIdentityAddress(_address);
        return _storage.claims[idObj][_claimTopic];
    }

    function hasClaim(address _address, uint256 _claimTopic) external view override returns (bool) {
        IdentitySystemStorage.IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        address idObj = _getIdentityAddress(_address);
        for (uint256 i = 0; i < _storage.claimList[idObj].length; i++) {
            if (_storage.claimList[idObj][i] == _claimTopic) {
                return true;
            }
        }
        return false;
    }
}
","keccak256":"0x6c83fe5fa8f88eef3ef7d84dc5b1440798fec27321825356501f9cf2952cb15f","license":"MIT"},"contracts/identity/IdentityStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../interfaces/IERC721A.sol";
import {TrustedIssuer} from "../interfaces/ITrustedIssuersRegistry.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/interfaces/IERC721Receiver.sol";
import "@openzeppelin/contracts/interfaces/IERC721.sol";
import {IIdentity} from "../interfaces/IIdentity.sol";
import {Claim} from "../interfaces/IClaim.sol";
import "../interfaces/IMarketplace.sol";
import {MultiSaleStorage} from "../interfaces/IMultiSale.sol";
import "../libraries/TradeDealLib.sol";
import "../libraries/FeeDistributorLib.sol"; // Add import for FeeDistributorLib

import "hardhat/console.sol";

library IdentitySystemStorage {
    using IdentitySystemStorage for IdentitySystemStorage.IdentitySystem;
    using Address for address;
bytes32 internal constant MULTISALE_STORAGE_POSITION =
        keccak256("diamond.nextblock.bitgem.app.MultiSaleStorage.storage");

    function multiSaleStorage()
        internal
        pure
        returns (MultiSaleStorage storage ds)
    {
        bytes32 position = MULTISALE_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    // CollateralStorage struct definition
    struct CollateralStorage {
        address nft;
        address invoicePool;
        address vabb;
        address usdcPool;
        address interest;
        uint256 interestRate;
        uint256 collateralToInterestRatio;
    }

    error ApprovalCallerNotOwnerNorApproved();
    error ApprovalQueryForNonexistentToken();
    error ApproveToCaller();
    error ApprovalToCurrentOwner();
    error BalanceQueryForZeroAddress();
    error MintedQueryForZeroAddress();
    error BurnedQueryForZeroAddress();
    error AuxQueryForZeroAddress();
    error MintToZeroAddress();
    error MintZeroQuantity();
    error OwnerIndexOutOfBounds();
    error OwnerQueryForNonexistentToken();
    error TokenIndexOutOfBounds();
    error TransferCallerNotOwnerNorApproved();
    error TransferFromIncorrectOwner();
    error TransferToNonERC721ReceiverImplementer();
    error TransferToZeroAddress();
    error URIQueryForNonexistentToken();

    struct IdentitySystem {
        // ERC721A storage
        ERC721AContract erc721Contract;
        // TrustedIssuer storage
        mapping(address => TrustedIssuer) trustedIssuers;
        address[] trustedIssuerAddresses;
        mapping(address => mapping(uint256 => bool)) trustedIssuerClaimTopics;
        mapping(address => uint256[]) trustedIssuerClaimTopicsList;
        mapping(uint256 => bool) topics;
        uint256[] topicList;
        // Identity storage
        mapping(address => address) identities;
        mapping(address => address) identityOwnersMap;
        address[] identityOwners;
        // Claim storage
        mapping(address => mapping(uint256 => uint256)) claims;
        mapping(address => uint256[]) claimList;
        // Claim storage
        mapping(uint256 => Claim) tokenClaims;
        Claim[] tokenClaimList;
        // ERC721Permissioned storage
        address delegatedMinter;
        mapping(uint256 => bool) frozen;

        uint256 itemIds;
        uint256 itemsSold;
        mapping(uint256 => MarketItem) idToMarketItem;
        mapping(uint256 => bool) idToListed;
        MarketItem[] items;

        // Merkle proof tracking for MultiSale
        mapping(bytes32 => bool) usedMerkleLeaves;

        // Collateral storage
        CollateralStorage collateralStorage;

        // Trade Deal storage
        TradeDealLib.TradeDealStorage tradeDealStorage;

        // Fee Distributor storage
        FeeDistributorLib.FeeDistributorStorage feeDistributorStorage;
    }

    bytes32 constant IDENTITY_SYSTEM_STORAGE_POSITION =
        keccak256("diamond.identity.system.storage");

    function diamondStorage()
        internal
        pure
        returns (IdentitySystemStorage.IdentitySystem storage ds)
    {
        bytes32 position = IDENTITY_SYSTEM_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function _tokenExists(
        IdentitySystem storage self,
        uint256 _tokenId
    ) internal view returns (bool) {
        return self.erc721Contract._ownerships[_tokenId].addr != address(0);
    }

    function _getIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (address addr) {
        return self.identities[_userAddress];
    }

    function getIdentityOwner(
        IdentitySystem storage self,
        address _identity
    ) internal view returns (address) {
        return self.identityOwnersMap[_identity];
    }

    function isIdentityAddress(
        IdentitySystem storage self,
        address _identity
    ) internal view returns (bool) {
        return self.identityOwnersMap[_identity] != address(0);
    }

    function isRegistered(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return self.identities[_userAddress] != address(0);
    }

    function isIdentityOwner(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return
            self.identityOwnersMap[self.identities[_userAddress]] ==
            _userAddress;
    }

    // trusted issuer

    function isTrustedIssuer(
        IdentitySystem storage self,
        address _issuer
    ) internal view returns (bool) {
        return self.trustedIssuers[_issuer].claimIssuer != address(0);
    }

    function addTrustedIssuer(
        IdentitySystem storage self,
        address _trustedIssuer,
        uint[] calldata _claimTopics
    ) internal {
        self.trustedIssuers[_trustedIssuer] = TrustedIssuer({
            claimIssuer: _trustedIssuer,
            claimTopics: _claimTopics
        });
        self.trustedIssuerAddresses.push(_trustedIssuer);
    }

    function removeTrustedIssuer(
        IdentitySystem storage self,
        address _trustedIssuer
    ) internal {
        delete self.trustedIssuers[_trustedIssuer];
        for (uint256 i = 0; i < self.trustedIssuerAddresses.length; i++) {
            if (self.trustedIssuerAddresses[i] == _trustedIssuer) {
                self.trustedIssuerAddresses[i] = self.trustedIssuerAddresses[
                    self.trustedIssuerAddresses.length - 1
                ];
                self.trustedIssuerAddresses.pop();
                break;
            }
        }
    }

    // claim topics

    function addClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal {
        self.topics[_claimTopic] = true;
        self.topicList.push(_claimTopic);
    }

    function removeClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal {
        delete self.topics[_claimTopic];
        for (uint256 i = 0; i < self.topicList.length; i++) {
            if (self.topicList[i] == _claimTopic) {
                self.topicList[i] = self.topicList[self.topicList.length - 1];
                self.topicList.pop();
                break;
            }
        }
    }

    function getClaimTopics(
        IdentitySystem storage self
    ) internal view returns (uint256[] memory) {
        return self.topicList;
    }

    function hasClaimTopic(
        IdentitySystem storage self,
        uint256 _claimTopic
    ) internal view returns (bool) {
        return self.topics[_claimTopic];
    }

    // trusted issuer / claim topics

    function updateIssuerClaimTopics(
        IdentitySystem storage self,
        address _trustedIssuer,
        uint[] calldata _claimTopics
    ) internal {
        self.trustedIssuers[_trustedIssuer].claimTopics = _claimTopics;
        for (
            uint256 i = 0;
            i < self.trustedIssuerClaimTopicsList[_trustedIssuer].length;
            i++
        ) {
            delete self.trustedIssuerClaimTopics[_trustedIssuer][
                self.trustedIssuerClaimTopicsList[_trustedIssuer][i]
            ];
        }
    }

    function addTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint _claimTopic
    ) internal {
        self.trustedIssuerClaimTopics[_issuer][_claimTopic] = true;
        self.trustedIssuerClaimTopicsList[_issuer].push(_claimTopic);
    }

    function removeTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint _claimTopic
    ) internal {
        delete self.trustedIssuerClaimTopics[_issuer][_claimTopic];
        for (
            uint256 i = 0;
            i < self.trustedIssuerClaimTopicsList[_issuer].length;
            i++
        ) {
            if (self.trustedIssuerClaimTopicsList[_issuer][i] == _claimTopic) {
                self.trustedIssuerClaimTopicsList[_issuer][i] = self
                    .trustedIssuerClaimTopicsList[_issuer][
                        self.trustedIssuerClaimTopicsList[_issuer].length - 1
                    ];
                self.trustedIssuerClaimTopicsList[_issuer].pop();
                break;
            }
        }
    }

    modifier isTrustedIssuerForClaimTopic(uint256 _claimTopic) {
        IdentitySystem storage _storage = IdentitySystemStorage.diamondStorage();
        require(_storage.isTrustedIssuer(msg.sender), "Not a trusted issuer");
        require(
            _storage.hasTrustedIssuerClaimTopic(msg.sender, _claimTopic),
            "Issuer not authorized for claim topic"
        );
        _;
    }

    function hasTrustedIssuerClaimTopic(
        IdentitySystem storage self,
        address _issuer,
        uint256 _claimTopic
    ) internal view returns (bool) {
        if (!self.isTrustedIssuer(_issuer)) return false;
        uint256[] storage claimTopics = self
            .trustedIssuers[_issuer]
            .claimTopics;
        for (uint256 i = 0; i < claimTopics.length; i++) {
            if (claimTopics[i] == _claimTopic) {
                return true;
            }
        }
        return false;
    }

    function addIdentity(
        IdentitySystem storage self,
        address _userAddress,
        address _identity
    ) internal {
        self.identities[_userAddress] = _identity;
        self.identityOwners.push(_userAddress);
        self.identityOwnersMap[_identity] = _userAddress;
    }

    function removeIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal {
        address identityAddress = self.identities[_userAddress]; // Capture identity address before deletion
        delete self.identities[_userAddress];
        for (uint256 i = 0; i < self.identityOwners.length; i++) {
            if (self.identityOwners[i] == _userAddress) {
                self.identityOwners[i] = self.identityOwners[
                    self.identityOwners.length - 1
                ];
                self.identityOwners.pop();
                self.identityOwnersMap[identityAddress] = address(0); // Use captured identityAddress
                break;
            }
        }
    }

    function hasIdentity(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        address identity = self.identities[_userAddress];
        return identity != address(0);
    }

    function hasClaims(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return self.identities[_userAddress] != address(0);
    }

    function isVerified(
        IdentitySystem storage self,
        address _userAddress
    ) internal view returns (bool) {
        return hasIdentity(self, _userAddress) && hasClaims(self, _userAddress);
    }

    /**
     * @dev Mints `quantity` tokens and transfers them to `to`.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - `quantity` must be greater than 0.
     *
     * Emits a {Transfer} event.
     */
    function _mint(
        IdentitySystem storage self,
        address msgSender,
        address to,
        uint256 quantity,
        bytes memory _data,
        bool safe
    ) internal {
        // Overflows are incredibly unrealistic.
        // balance or numberMinted overflow if current value of either + quantity > 1.8e19 (2**64) - 1
        // updatedIndex overflows if _currentIndex + quantity > 1.2e77 (2**256) - 1
        uint256 startTokenId = self.erc721Contract._currentIndex;
        if (to == address(0)) revert MintToZeroAddress();
        if (quantity == 0) revert MintZeroQuantity();

        self._beforeTokenTransfer(address(0), to, startTokenId, true);

        unchecked {
            self.erc721Contract._addressData[to].balance += uint64(quantity);
            self.erc721Contract._addressData[to].numberMinted += uint64(quantity);

            self.erc721Contract._ownerships[startTokenId].addr = to;
            self.erc721Contract._ownerships[startTokenId].startTimestamp = uint64(
                block.timestamp
            );

            uint256 updatedIndex = startTokenId;

            // log quantity

            for (uint256 i; i < quantity; i++) {
                emit Transfer(address(0), to, updatedIndex);
                if (
                    safe &&
                    !_checkOnERC721Received(
                        msgSender,
                        address(0),
                        to,
                        updatedIndex,
                        _data
                    )
                ) {
                    revert TransferToNonERC721ReceiverImplementer();
                }
                self.erc721Contract._currentIndex = updatedIndex + 1;
                updatedIndex++;
                self._addTokenToAllTokensEnumeration(updatedIndex); // Add this line
            }
        }

        _afterTokenTransfer(self, address(0), to, startTokenId, quantity);
    }

    /**
     * @dev See {IERC721-balanceOf}.
     */
    function balanceOf(
        IdentitySystem storage self,
        address owner
    ) internal view returns (uint256) {
        if (owner == address(0)) revert BalanceQueryForZeroAddress();
        return uint256(self.erc721Contract._addressData[owner].balance);
    }

    /**
     * Returns the number of tokens minted by `owner`.
     */
    function _numberMinted(
        IdentitySystem storage self,
        address owner
    ) internal view returns (uint256) {
        if (owner == address(0)) revert MintedQueryForZeroAddress();
        return uint256(self.erc721Contract._addressData[owner].numberMinted);
    }

    /**
     * Returns the number of tokens burned by or on behalf of `owner`.
     */
    function _numberBurned(
        IdentitySystem storage self,
        address owner
    ) internal view returns (uint256) {
        if (owner == address(0)) revert BurnedQueryForZeroAddress();
        return uint256(self.erc721Contract._addressData[owner].numberBurned);
    }

    /**
     * Returns the auxillary data for `owner`. (e.g. number of whitelist mint slots used).
     */
    function _getAux(
        IdentitySystem storage self,
        address owner
    ) internal view returns (uint64) {
        if (owner == address(0)) revert AuxQueryForZeroAddress();
        return self.erc721Contract._addressData[owner].aux;
    }

    /**
     * Sets the auxillary data for `owner`. (e.g. number of whitelist mint slots used).
     * If there are multiple variables, please pack them into a uint64.
     */
    function _setAux(
        IdentitySystem storage self,
        address owner,
        uint64 aux
    ) internal {
        if (owner == address(0)) revert AuxQueryForZeroAddress();
        self.erc721Contract._addressData[owner].aux = aux;
    }

    function ownershipOf(
        IdentitySystem storage self,
        uint256 tokenId
    ) internal view returns (TokenOwnership memory) {
        uint256 curr = tokenId;
        unchecked {
            if (curr < self.erc721Contract._currentIndex) {
                TokenOwnership memory ownership = self.erc721Contract._ownerships[curr];
                if (!ownership.burned) {
                    if (ownership.addr != address(0)) {
                        return ownership;
                    }
                    // Invariant:
                    // There will always be an ownership that has an address and is not burned
                    // before an ownership that does not have an address and is not burned.
                    // Hence, curr will not underflow.
                    while (true) {
                        curr--;
                        ownership = self.erc721Contract._ownerships[curr];
                        if (ownership.addr != address(0)) {
                            return ownership;
                        }
                    }
                }
            }
        }
        revert OwnerQueryForNonexistentToken();
    }

    /**
     * @dev Returns whether `tokenId` exists.
     *
     * Tokens can be managed by their owner or approved accounts via {approve} or {setApprovalForAll}.
     *
     * Tokens start existing when they are minted (`_mint`),
     */
    function _exists(
        IdentitySystem storage self,
        uint256 tokenId
    ) internal view returns (bool) {
        return
            tokenId < self.erc721Contract._currentIndex && !self.erc721Contract._ownerships[tokenId].burned;
    }

    /**
     * @dev See {IERC721-getApproved}.
     */
    function getApproved(
        IdentitySystem storage self,
        uint256 tokenId
    ) internal view returns (address) {
        if (!_exists(self, tokenId)) revert ApprovalQueryForNonexistentToken();
        return self.erc721Contract._tokenApprovals[tokenId];
    }

    /**
     * @dev See {IERC721-setApprovalForAll}.
     */
    function setApprovalForAll(
        IdentitySystem storage self,
        address sender,
        address operator,
        bool approved
    ) internal {
        self.erc721Contract._operatorApprovals[sender][operator] = approved;
        emit ApprovalForAll(sender, operator, approved);
    }

    /**
     * @dev See {IERC721-isApprovedForAll}.
     */
    function isApprovedForAll(
        IdentitySystem storage self,
        address owner,
        address operator
    ) internal view returns (bool) {
        return self.erc721Contract._operatorApprovals[owner][operator];
    }

    function currentIndex(
        IdentitySystem storage self
    ) internal view returns (uint256) {
        return self.erc721Contract._currentIndex;
    }

    /**
     * @dev Transfers `tokenId` from `from` to `to`.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - `tokenId` token must be owned by `from`.
     *
     * Emits a {Transfer} event.
     */
    function _transfer(
        IdentitySystem storage self,
        address msgSender,
        address from,
        address to,
        uint256 tokenId,
        bool _force
    ) internal {
        TokenOwnership memory prevOwnership = self.ownershipOf(tokenId);

        bool isApprovedOrOwner = (msgSender == prevOwnership.addr ||
            self.isApprovedForAll(prevOwnership.addr, msgSender) ||
            self.getApproved(tokenId) == msgSender);

        if (!isApprovedOrOwner && !_force)
            revert TransferCallerNotOwnerNorApproved();
        if (prevOwnership.addr != from && !_force) revert TransferFromIncorrectOwner();
        if (to == address(0)) revert TransferToZeroAddress();

        self._beforeTokenTransfer(from, to, tokenId, _force);

        if (from == address(0)) {
            self._addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            self._removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0)) {
            self._removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            self._addTokenToOwnerEnumeration(to, tokenId);
        }

        // Clear approvals from the previous owner
        self._approve(address(0), tokenId, prevOwnership.addr);

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        // Counter overflow is incredibly unrealistic as tokenId would have to be 2**256.
        unchecked {
            self.erc721Contract._addressData[from].balance -= 1;
            self.erc721Contract._addressData[to].balance += 1;

            self.erc721Contract._ownerships[tokenId].addr = to;
            self.erc721Contract._ownerships[tokenId].startTimestamp = uint64(block.timestamp);

            // If the ownership slot of tokenId+1 is not explicitly set, that means the transfer initiator owns it.
            // Set the slot of tokenId+1 explicitly in storage to maintain correctness for ownerOf(tokenId+1) calls.
            uint256 nextTokenId = tokenId + 1;
            if (self.erc721Contract._ownerships[nextTokenId].addr == address(0)) {
                // This will suffice for checking _exists(nextTokenId),
                // as a burned slot cannot contain the zero address.
                if (nextTokenId < self.erc721Contract._currentIndex) {
                    self.erc721Contract._ownerships[nextTokenId].addr = prevOwnership.addr;
                    self.erc721Contract._ownerships[nextTokenId].startTimestamp = prevOwnership
                        .startTimestamp;
                }
            }
        }

        emit Transfer(from, to, tokenId);
        _afterTokenTransfer(self, from, to, tokenId, 1);
    }

    /**
     * @dev Destroys `tokenId`.
     * The approval is cleared when the token is burned.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     *
     * Emits a {Transfer} event.
     */
    function _burn(IdentitySystem storage self, address from, uint256 tokenId) internal {
        self._beforeTokenTransfer(from, address(0), tokenId, true);

        // Clear approvals from the previous owner
        _approve(self, address(0), tokenId, from);

        self._removeTokenFromOwnerEnumeration(from, tokenId);
        self._removeTokenFromAllTokensEnumeration(tokenId);

        unchecked {
            self.erc721Contract._addressData[from].balance -= 1;
            self.erc721Contract._addressData[from].numberBurned += 1;

            // Keep track of who burned the token, and the timestamp of burning.
            self.erc721Contract._ownerships[tokenId].addr = from;
            self.erc721Contract._ownerships[tokenId].startTimestamp = uint64(block.timestamp);
            self.erc721Contract._ownerships[tokenId].burned = true;

            // If the ownership slot of tokenId+1 is not explicitly set, that means the burn initiator owns it.
            // Set the slot of tokenId+1 explicitly in storage to maintain correctness for ownerOf(tokenId+1) calls.
            uint256 nextTokenId = tokenId + 1;
            if (self.erc721Contract._ownerships[nextTokenId].addr == address(0)) {
                // This will suffice for checking _exists(nextTokenId),
                // as a burned slot cannot contain the zero address.
                if (nextTokenId < self.erc721Contract._currentIndex) {
                    self.erc721Contract._ownerships[nextTokenId].addr = from;
                    self.erc721Contract._ownerships[nextTokenId].startTimestamp = self.erc721Contract._ownerships[tokenId].startTimestamp;
                }
            }
        }
        emit Transfer(from, address(0), tokenId);
        _afterTokenTransfer(self, from, address(0), tokenId, 1);

        // Overflow not possible, as _burnCounter cannot be exceed _currentIndex times.
        unchecked {
            self.erc721Contract._burnCounter++;
        }
    }

    function exists(IdentitySystem storage self, uint256 tokenId) internal view returns (bool) {
        return _exists(self, tokenId);
    }


    function setTokenClaimTopics(IdentitySystem storage self, uint256 tokenId, uint256[] memory claimTopics) internal {
        require(self.exists(tokenId), "ERC721: token does not exist");
        for (uint256 i = 0; i < claimTopics.length; i++) {
            self.tokenClaims[tokenId] = Claim({
                topic: claimTopics[i],
                scheme: 0,
                issuer: address(this),
                signature: "",
                data: "",
                uri: ""
            });
        }
    }

    function getTokenClaimTopics(uint256 tokenId) public view returns (Claim[] memory) {
        IdentitySystemStorage.IdentitySystem storage identitySystem = IdentitySystemStorage.diamondStorage();
        require(identitySystem._exists(tokenId), "ERC721: token does not exist");
        Claim[] memory claimTopics = new Claim[](identitySystem.tokenClaimList.length);
        for (uint256 i = 0; i < identitySystem.tokenClaimList.length; i++) {
            claimTopics[i] = identitySystem.tokenClaims[tokenId];
        }
        return claimTopics;
    }

    /**
     * @dev Approve `to` to operate on `tokenId`
     *
     * Emits a {Approval} event.
     */
    function _approve(
        IdentitySystem storage self,
        address to,
        uint256 tokenId,
        address owner
    ) internal {
        self.erc721Contract._tokenApprovals[tokenId] = to;
        emit Approval(owner, to, tokenId);
    }

    function _ownerOf(
        IdentitySystem storage self,
        uint256 tokenId
    ) internal view returns (address) {
        return ownershipOf(self, tokenId).addr;
    }

    function _isContract(
        address account
    ) internal view returns (bool) {
        // According to EIP-1052, an address is a contract if it has code.
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    /**
     * @dev Internal function to invoke {IERC721Receiver-onERC721Received} on a target address.
     * The call is not executed if the target address is not a contract.
     *
     * @param from address representing the previous owner of the given token ID
     * @param to target address that will receive the tokens
     * @param tokenId uint256 ID of the token to be transferred
     * @param _data bytes optional data to send along with the call
     * @return bool whether the call correctly returned the expected magic value
     */
    function _checkOnERC721Received(
        address msgSender,
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) internal returns (bool) {
        if (_isContract(to)) {
            try
                IERC721Receiver(to).onERC721Received(
                    msgSender,
                    from,
                    tokenId,
                    _data
                )
            returns (bytes4 retval) {
                return retval == IERC721Receiver(to).onERC721Received.selector;
            } catch (bytes memory reason) {
                if (reason.length == 0) {
                    revert TransferToNonERC721ReceiverImplementer();
                } else {
                    assembly {
                        revert(add(32, reason), mload(reason))
                    }
                }
            }
        } else {
            return true;
        }
    }

    function tokensOfOwner(
        IdentitySystem storage self,
        address owner
    ) internal view returns (uint256[] memory) {
        require(owner != address(0), "IdentityStorage: owner query for zero address");

        uint256 ownerTokenCount = IERC721(address(this)).balanceOf(owner);
        if (ownerTokenCount == 0) {
            // Return an empty array
            return new uint256[](0);
        }

        uint256[] memory tokens = new uint256[](ownerTokenCount);
        for (uint256 i = 0; i < ownerTokenCount; i++) {
            tokens[i] = self.erc721Contract._ownedTokens[owner][i];
        }

        return tokens;
    }

    function tokenOfOwnerByIndex(
        IdentitySystem storage self,
        address owner, 
        uint256 index) internal view returns (uint256) {
        require(index < IERC721(address(this)).balanceOf(owner), "ERC721Enumerable: owner index out of bounds");
        return self.erc721Contract._ownedTokens[owner][index];
    }

    /**
     * @dev See {IERC721Enumerable-totalSupply}.
     */
    function totalSupply(
        IdentitySystem storage self
    ) internal view returns (uint256) {
        return self.erc721Contract._allTokens.length;
    }

    /**
     * @dev See {IERC721Enumerable-tokenByIndex}.
     */
    function tokenByIndex(
        IdentitySystem storage self,
        uint256 index
    ) internal view returns (uint256) {
        require(index < totalSupply(self), "ERC721Enumerable: global index out of bounds");
        return self.erc721Contract._allTokens[index];
    }

    /**
     * @dev Private function to add a token to this extension's ownership-tracking data structures.
     * @param to address representing the new owner of the given token ID
     * @param tokenId uint256 ID of the token to be added to the tokens list of the given address
     */
    function _addTokenToOwnerEnumeration(
        IdentitySystem storage self,
        address to, 
        uint256 tokenId) internal {
        uint256 length = IERC721(address(this)).balanceOf(to);
        self.erc721Contract._ownedTokens[to][length] = tokenId;
        self.erc721Contract._ownedTokensIndex[tokenId] = length;
    }

    /**
     * @dev Private function to add a token to this extension's token tracking data structures.
     * @param tokenId uint256 ID of the token to be added to the tokens list
     */
    function _addTokenToAllTokensEnumeration(
        IdentitySystem storage self,
        uint256 tokenId) internal {
        self.erc721Contract._allTokensIndex[tokenId] = self.erc721Contract._allTokens.length + 1;
        self.erc721Contract._allTokens.push(tokenId);
    }

    function _getIdentityAddress(IdentitySystem storage self, address _address) internal view returns (address) {
        address identityAddress = self._getIdentity(_address);
        return identityAddress != address(0) ? identityAddress : _address;
    }

    function _hasClaim(IdentitySystem storage self, address _address, uint256 _claimTopic) internal view returns (bool) {
        address idObj = self._getIdentityAddress(_address);
        for (uint256 i = 0; i < self.claimList[idObj].length; i++) {
            if (self.claimList[idObj][i] == _claimTopic) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Private function to remove a token from this extension's ownership-tracking data structures. Note that
     * while the token is not assigned a new owner, the `_ownedTokensIndex` mapping is _not_ updated: this allows for
     * gas optimizations e.g. when performing a transfer operation (avoiding double writes).
     * This has O(1) time complexity, but alters the order of the _ownedTokens array.
     * @param from address representing the previous owner of the given token ID
     * @param tokenId uint256 ID of the token to be removed from the tokens list of the given address
     */
    function _removeTokenFromOwnerEnumeration(
        IdentitySystem storage self,
        address from,
        uint256 tokenId
    ) internal {
        // address owner = IERC721(address(this)).ownerOf(tokenId);
        address thisAddress = address(this);


        // require(owner == thisAddress, "ERC721: transfer of token that is not own");
        uint256 lastTokenIndex = IERC721(thisAddress).balanceOf(from) - 1; 
        uint256 tokenIndex = self.erc721Contract._ownedTokensIndex[tokenId];

        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = self.erc721Contract._ownedTokens[from][lastTokenIndex];

            self.erc721Contract._ownedTokens[from][tokenIndex] = lastTokenId;
            self.erc721Contract._ownedTokensIndex[lastTokenId] = tokenIndex;
        }

        delete self.erc721Contract._ownedTokensIndex[tokenId];
        delete self.erc721Contract._ownedTokens[from][lastTokenIndex];
    }

    /**
     * @dev Private function to remove a token from this extension's token tracking data structures.
     * This has O(1) time complexity, but alters the order of the _allTokens array.
     * @param tokenId uint256 ID of the token to be removed from the tokens list
     */
    function _removeTokenFromAllTokensEnumeration(
        IdentitySystem storage self,
        uint256 tokenId
    ) internal {
        // To prevent a gap in the tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).
        uint256 lastTokenIndex = self.erc721Contract._allTokens.length - 1;
        uint256 tokenIndex = self.erc721Contract._allTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary. However, since this occurs so
        // rarely (when the last minted token is burnt) that we still do the swap here to avoid the gas cost of adding
        // an 'if' statement (like in _removeTokenFromOwnerEnumeration)
        uint256 lastTokenId = self.erc721Contract._allTokens[lastTokenIndex];

        self.erc721Contract._allTokens[tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
        self.erc721Contract._allTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index

        // This also deletes the contents at the last position of the array
        delete self.erc721Contract._allTokensIndex[tokenId];
        self.erc721Contract._allTokens.pop();
    }

    /**
     * @dev Hook that is called before a set of serially-ordered token ids are about to be transferred. This includes minting.
     * And also called before burning one token.
     *
     * startTokenId - the first token id to be transferred
     * quantity - the amount to be transferred
     *
     * Calling conditions:
     *
     * - When `from` and `to` are both non-zero, `from`'s `tokenId` will be
     * transferred to `to`.
     * - When `from` is zero, `tokenId` will be minted for `to`.
     * - When `to` is zero, `tokenId` will be burned by `from`.
     * - `from` and `to` are never both zero.
     */

    function _beforeTokenTransfer(
        IdentitySystem storage self,
        address from,
        address to,
        uint256,
        bool force
    ) internal view {
        // Allow transfers from or to the contract itself without additional checks
        if(force) return;

        bool hasCommonClaim = false;
        uint256 claimType = 1; // Start from claim type 1

        while (!hasCommonClaim) {
            bool hasFromClaim = self._hasClaim(from, claimType) ||
                from == address(this);
            if (hasFromClaim && self._hasClaim(to, claimType)) {
                hasCommonClaim = true;
            } else {
                claimType++;
                if (claimType == 0) {
                    // Overflow, we've checked all possible claim types
                    break;
                }
            }
        }

        require(
            hasCommonClaim,
            "ERC721: sender and receiver do not have a common claim"
        );
    }

    /**
     * @dev Hook that is called after a set of serially-ordered token ids have been transferred. This includes
     * minting.
     * And also called after one token has been burned.
     *
     * startTokenId - the first token id to be transferred
     * quantity - the amount to be transferred
     *
     * Calling conditions:
     *
     * - When `from` and `to` are both non-zero, `from`'s `tokenId` has been
     * transferred to `to`.
     * - When `from` is zero, `tokenId` has been minted for `to`.
     * - When `to` is zero, `tokenId` has been burned by `from`.
     * - `from` and `to` are never both zero.
     */
    function _afterTokenTransfer(
        IdentitySystem storage self,
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal {}
}
