pragma solidity ^0.8.0;

/**
 * @title IFeeDistributor
 * @dev Interface for a fee distribution system.
 */
interface IFeeDistributor {
    /**
     * @notice Sets the addresses that will receive fees and their corresponding weights.
     * @dev Weights determine the proportion of fees each receiver gets.
     * @param _feeReceivers Array of addresses to receive fees.
     * @param _feeWeights Array of weights corresponding to each receiver. Must sum to a specific value (e.g., 10000 for basis points).
     */
    function setFeeReceivers(
        address[] calldata _feeReceivers,
        uint256[] calldata _feeWeights
    ) external;

    /**
     * @notice Gets the current fee receivers and their weights.
     * @return feeReceivers Array of addresses currently set to receive fees.
     * @return feeWeights Array of weights corresponding to each receiver.
     */
    function getFeeReceivers()
        external
        view
        returns (
            address[] memory feeReceivers,
            uint256[] memory feeWeights
        );

    /**
     * @notice Calculates the adjusted principal amount and individual fee amounts based on the total principal.
     * @dev This function is view-only and does not perform any state changes or transfers.
     * @param principalAmount The total amount before fee deduction.
     * @return adjustedAmount The principal amount remaining after deducting total fees.
     * @return feeAmounts Array of individual fee amounts calculated for each receiver.
     */
    function calculateAmounts(uint256 principalAmount) 
        external
        view
        returns (
            uint256 adjustedAmount,
            uint256[] memory feeAmounts
        );

    /**
     * @notice Distributes a principal amount, sending calculated fees to receivers and the adjusted amount to the principal receiver.
     * @dev Requires the contract to hold sufficient funds (e.g., USDC) to perform the transfers.
     * @param principalAmountReceiver The address to receive the adjusted principal amount.
     * @param _principalAmount The total amount to be distributed.
     * @return adjustedAmountReceiver The address that received the adjusted principal amount.
     * @return adjustedAmount The adjusted principal amount transferred.
     * @return feeReceivers The addresses that received the fee amounts.
     * @return feeAmounts The individual fee amounts transferred.
     */
    function distributeAmounts(
        address principalAmountReceiver,
        uint256 _principalAmount
    ) external returns (
        address adjustedAmountReceiver,
        uint256 adjustedAmount,
        address[] memory feeReceivers,
        uint256[] memory feeAmounts
    );

    /**
     * @dev Emitted when the fee receivers and weights are updated.
     * @param feeReceivers The new array of fee receiver addresses.
     * @param feeWeights The new array of corresponding fee weights.
     */
    event FeeReceiversSet(
        address[] feeReceivers,
        uint256[] feeWeights
    );

    /**
     * @dev Emitted when principal and fee amounts are successfully distributed.
     * @param principalAmountReceiver The address that received the adjusted principal amount.
     * @param principalAmount The original principal amount before fee deduction.
     * @param adjustedAmountReceiver The address that received the adjusted principal amount (same as principalAmountReceiver).
     * @param adjustedAmount The adjusted principal amount transferred.
     * @param feeReceivers The addresses that received the fee amounts.
     * @param feeAmounts The individual fee amounts transferred.
     */
    event AmountsDistributed(
        address principalAmountReceiver,
        uint256 principalAmount,
        address adjustedAmountReceiver,
        uint256 adjustedAmount,
        address[] feeReceivers,
        uint256[] feeAmounts
    );

    /**
     * @dev Emitted when the Fee Distributor is initialized.
     * @param distributionToken The address of the ERC20 token set for distribution.
     * @param totalWeightBasis The basis used for calculating fee weights (e.g., 10000).
     */
    event FeeDistributorInitialized(
        address indexed distributionToken,
        uint256 totalWeightBasis
    );
}","keccak256":"0x39f54a07e1e1a958b94e58c96b1bd287f3b7bedf40f310b1c1bd5e678297524e","license":"MIT"},"contracts/interfaces/IIdentity.sol":{"content":"// SPDX-License-Identifier: MIT
