pragma solidity ^0.8.0;
import "../identity/IdentityStorage.sol";
import "../interfaces/ICollateral.sol";
import "../interfaces/IInterest.sol";
import "../interfaces/IUSDCPool.sol";
import "../interfaces/IAttribute.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
using SafeERC20 for IERC20;
import "../interfaces/IERC721Permissioned.sol";
import "../interfaces/IFeeDistributor.sol"; // Added for fee distribution
import "hardhat/console.sol";
import "../libraries/LibDiamond.sol";
import "../libraries/AttributeLib.sol";

import {console} from "hardhat/console.sol";

library TradeDealLib {
    // These event declarations are temporary until we complete migration
    // They'll be removed once we've updated all functions to return event params
    // rather than emit events directly

    // Operation mode for trade deals
    enum OperationMode {
        CENTRALIZED,    // Contract owner manages all fund operations
        SELF_SERVICE,   // Borrowers can directly withdraw/repay funds
        HYBRID,         // Mixed model with configurable permissions
        CUSTOM          // Fine-grained permission configuration
    }

    // Roles for the role-based access control system
    enum Role {
        NONE,        // No special role (default)
        ADMIN,       // Full control over the trade deal
        LENDER,      // Can deposit funds and receive Collateral tokens
        BORROWER,    // Can deposit invoices and withdraw funds (in self-service)
        UNDERWRITER, // Can approve/reject deals, modify terms
        LIQUIDATOR   // Can liquidate collateral if terms are violated
    }

    // Permission bit flags for fine-grained control
    uint256 constant PERMISSION_DEPOSIT_FUNDS = 1;
    uint256 constant PERMISSION_WITHDRAW_FUNDS = 2;
    uint256 constant PERMISSION_DEPOSIT_COLLATERAL = 4;
    uint256 constant PERMISSION_WITHDRAW_COLLATERAL = 8;
    uint256 constant PERMISSION_DISTRIBUTE_INTEREST = 16;

    event TradeDealCreated(
        uint256 indexed tradeDealId,
        string name,
        string symbol,
        uint256 interestRate,
        uint256 collateralToInterestRatio,
        bool active,
        address nftAddress,
        address collateralAddress,
        address interestAddress,
        address usdcAddress
    );
    // Removed duplicate TradeDealUpdated event definition
    event TradeDealActivated(uint256 indexed tradeDealId);
    event TradeDealDeactivated(uint256 indexed tradeDealId);
    event TradeDealParticipantAdded(uint256 indexed tradeDealId, address indexed participant);
    event TradeDealParticipantRemoved(uint256 indexed tradeDealId, address indexed participant);
    event InvoiceDepositedToTradeDeal(uint256 indexed tradeDealId, uint256 indexed tokenId);
    event InvoiceWithdrawnFromTradeDeal(uint256 indexed tradeDealId, uint256 indexed tokenId);
    event USDCDepositedToTradeDeal(uint256 indexed tradeDealId, uint256 amount);
    event USDCWithdrawnFromTradeDeal(uint256 indexed tradeDealId, uint256 amount);
    event InterestDistributedForTradeDeal(uint256 indexed tradeDealId, uint256 totalInterest, uint256 invoicePoolInterest, uint256 interestInterest, uint256 interestTokensMinted);
    event TradeDealRequiredClaimTopicsSet(uint256 indexed tradeDealId, uint256[] claimTopics);
    // New events for enhanced functionality
    event TradeDealFullyFunded(uint256 indexed tradeDealId, uint256 fundingTarget);
    event TradeDealFundingWithdrawn(uint256 indexed tradeDealId, address indexed recipient, uint256 amount);
    event TradeDealRepaid(uint256 indexed tradeDealId, address indexed repayer, uint256 amount, bool fullyRepaid);
    event CollateralTokensRedeemed(uint256 indexed tradeDealId, address indexed redeemer, uint256 collateralAmount, uint256 usdcAmount);
    // Trade Deal data structure
    struct TradeDeal {
        uint256 id;
        string name;
        string symbol;           // Symbol for the trade deal, used for Collateral token
        uint256 interestRate;
        uint256 collateralToInterestRatio;
        bool active;
        uint256[] requiredClaimTopics; // Claim topics required for participation
        address collateralAddress;     // Address of the Collateral token contract for this trade deal
        address interestAddress;     // Address of the VABI token contract for this trade deal
        address usdcAddress;     // Address of the USDC token contract for this trade deal
        OperationMode operationMode; // Operation mode (CENTRALIZED or SELF_SERVICE)
    }

    struct CollateralRedemptionResult {
        uint256 tradeDealId;
        address redeemer;
        uint256 collateralAmount;
        uint256 usdcAmount;
    }

    // Extended CollateralleStorage to support trade deals
    struct TradeDealStorage {
        // Trade deal tracking
        mapping(uint256 => TradeDeal) tradeDeals;
        uint256[] tradeDealIds;
        uint256 nextTradeDealId;

        // Per-trade deal mappings
        mapping(uint256 => uint256[]) tradeDealInvoices; // Trade deal ID => array of invoice token IDs
        mapping(uint256 => uint256) tradeDealUsdcBalances; // Trade deal ID => USDC balance
        mapping(uint256 => mapping(address => bool)) tradeDealParticipants; // Trade deal ID => user address => is participant
        mapping(uint256 => uint256[]) tradeDealRequiredClaimTopics; // Trade deal ID => array of required claim topics

        // Role-based access control
        mapping(uint256 => mapping(address => Role)) userRoles; // Trade deal ID => user address => role
        mapping(uint256 => mapping(address => uint256)) userPermissions; // Trade deal ID => user address => permissions

        // New fields for enhanced functionality
        mapping(uint256 => uint256) tradeDealFundingTargets; // Trade deal ID => funding target
        mapping(uint256 => bool) tradeDealFundingWithdrawn; // Trade deal ID => whether funding has been withdrawn
        mapping(uint256 => uint256) tradeDealRepaidAmounts; // Trade deal ID => amount repaid
        mapping(uint256 => uint256) tradeDealTotalDebt; // Trade deal ID => total debt (principal + interest)

        // Track original depositors of invoices
        mapping(uint256 => mapping(uint256 => address)) invoiceDepositors; // Trade deal ID => token ID => original depositor
    }

    // Return struct for createTradeDeal function
    struct CreateTradeDealResult {
        uint256 tradeDealId;
        string name;
        string symbol;
        uint256 interestRate;
        uint256 collateralToInterestRatio;
        bool active;
        address nftAddress;
        address collateralAddress;
        address interestAddress;
        address usdcAddress;
        bool hasClaimTopics;
        uint256[] requiredClaimTopics;
        OperationMode operationMode;
    }

    // Parameters struct for createTradeDeal function
    struct CreateTradeDealParams {
        string name;
        string symbol;
        uint256 interestRate;
        uint256 collateralToInterestRatio;
        uint256[] requiredClaimTopics;
        address collateralAddress;
        address interestAddress;
        address usdcAddress;
        OperationMode operationMode;
